package Fuel.com;

public class Type_Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		Implicit (Automatic) : small->big
//		Explict (Manual) : big-> small
	
		int x = 10;
		int y = x;
		System.out.println(y);
		
		double d = 65.5;
		int z = (int)d;
		System.out.println(z);
		
	}

}
